import React, { useState, useEffect } from "react";
import axios from "axios";
import "./index.css"; // Import your CSS file

const API_BASE = "http://localhost:3001";

function App() {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    getTodos();
  }, []);

  const getTodos = () => {
    fetch(API_BASE + "/todos")
      .then((res) => res.json())
      .then((data) => setTodos(data))
      .catch((err) => console.error("Error: ", err));
  };

  const completeTodo = async (id) => {
    await axios
      .patch(`http://localhost:3001/todo/complete/${id}`)
      .then((res) => {
        console.log(res);
        console.log(res.data);
      });

    getTodos();
  };

  const deleteTodo = async (id) => {
    await axios
      .delete(`http://localhost:3001/todo/delete/${id}`)
      .then((res) => {
        console.log(res);
        console.log(res.data);
      });

    getTodos();
  };

      return (
        <main className="app">
  <nav className="nav">
    <a href="" className="nav__item active">All</a>
    <a href="" className="nav__item">Active</a>
    <a href="" className="nav__item">Complete</a>
  </nav>
  <div className="add">
    <div className="add__priority">
      <label className="add__radio" title="Priority 0">
        <input checked type="radio" name="priority"/>
        <span className="add__circle"></span>
      </label>
      <label className="add__radio add__radio--1" title="Priority 1">
        <input type="radio" name="priority"/>
        <span className="add__circle"></span>
      </label>
      <label className="add__radio add__radio--2" title="Priority 2">
        <input type="radio" name="priority"/>
        <span className="add__circle"></span>
      </label>
      <label className="add__radio add__radio--3" title="Priority 3">
        <input type="radio" name="priority"/>
        <span className="add__circle"></span>
      </label>
    </div>
    <input placeholder="+ Add todo item" type="text" className="add__input"/>
  </div>
  <ul className="list">

  {todos.map(todo => (
    <li className={"item " + (todo.complete ? "done": "")} key={todo._id}>
      <label className={`item__checkbox item__checkbox--${todo.priority}`}><input type="checkbox" oncl/><i className="fas fa-check"></i></label>{todo.text}<button className="item__delete"><i className="fas fa-trash-alt"></i></button></li>
  ))}
  </ul>
</main>
  )}

export default App;
