import React, { useState, useEffect } from "react";
import axios from "axios";
import "./index.css"; // Import your CSS file
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashAlt, faCheck } from '@fortawesome/free-solid-svg-icons';
import { BrowserRouter as Router, useLocation, Route, Switch } from 'react-router-dom';

const API_BASE = "http://localhost:3001";

function App() {
  const [todos, setTodos] = useState([]);
  const [checkState, setState] = useState([]);
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);

  console.log(searchParams.get('items'));

  useEffect(() => {
    getTodos();
  }, []);

  const getTodos = () => {
    fetch(API_BASE + "/todos")
      .then((res) => res.json())
      .then((data) => setTodos(data))
      .catch((err) => console.error("Error: ", err));
  };

  const completeTodo = async (id) => {
    await axios
      .patch(`http://localhost:3001/todo/complete/${id}`)
      .then((res) => {
        console.log(res);
        console.log(res.data);
      });

    getTodos();
  };

  const deleteTodo = async (id) => {
    await axios
      .delete(`http://localhost:3001/todo/delete/${id}`)
      .then((res) => {
        console.log(res);
        console.log(res.data);
      });

    getTodos();
  };

  const checkTodo = async (id) => {
    // Implement the checkTodo functionality if needed
  }

  return (
    <Router>
      <main className="app">
        <nav className="nav">
          <a href="" className="nav__item active">All</a>
          <a href="/?items=active" className="nav__item">Active</a>
          <a href="" className="nav__item">Complete</a>
        </nav>
        <div className="add">
          <div className="add__priority">
            <label className="add__radio" title="Priority 0">
              <input checked type="radio" name="priority" />
              <span className="add__circle"></span>
            </label>
            {/* ... other priority labels */}
          </div>
          <input placeholder="+ Add todo item" type="text" className="add__input" />
        </div>
        <ul className="list">
          {todos.map(todo => (
            <li className={"item " + (todo.complete ? "done" : "")} key={todo._id}>
              <label className={`item__checkbox item__checkbox--${todo.priority}`}>
                <input onClick={() => completeTodo(todo._id)} checked type="checkbox" />
                {todo.complete ? <FontAwesomeIcon icon={faCheck} /> : null}
              </label>
              {todo.text}
              <button className="item__delete" onClick={() => deleteTodo(todo._id)}>
                <FontAwesomeIcon icon={faTrashAlt} />
              </button>
            </li>
          ))}
        </ul>
      </main>
    </Router>
  );
}

export default App;
